            );
    
            address[] memory tokens;
            if (_startOnUniswap == true) {
                tokens[0] = _tokenA;
                tokens[1] = _tokenB;
    
                swapOnUniswap(_tokensFromFlashLoan, 10, tokens);
    
                tokens[0] = _tokenB;
                tokens[1] = _tokenA;
    
                swapOnSushiswap(
                    IERC20(tokens[0]).balanceOf(address(this)),
                    10,
                    tokens
