            } else {
                tokens[0] = _tokenA;
                tokens[1] = _tokenB;
    
                swapOnSushiswap(_tokensFromFlashLoan, 10, tokens);
    
                tokens[0] = _tokenB;
                tokens[1] = _tokenA;
    
                swapOnUniswap(
                    IERC20(tokens[0]).balanceOf(address(this)),
